import { useContext } from 'react'
import { GlobalStoreContext } from '../store';
import { useHistory } from 'react-router-dom'

function DeleteSongModal() {
    const { store } = useContext(GlobalStoreContext);
    const history = useHistory();

    //let name = store.currentList.songs;
    let name = ""
    return (
        <div 
            className="modal" 
            id="delete-song-modal" 
            data-animation="slideInOutLeft">
                <div className="modal-root" id='verify-delete-song-root'>
                    <div className="modal-north">
                        Delete song?
                    </div>
                    <div className="modal-center">
                        <div id="delete-song-modal-center" className="modal-center-content">
                            Are you sure you wish to permanently delete {(store.currentList && store.songForDeletion) ? store.currentList.songs[store.songForDeletion].title : ""}
                        </div>
                    </div>
                    <div className="modal-south">
                        <input type="button" 
                            id="delete-song-confirm-button" 
                            className="modal-button" 
                            onClick={store.createDeleteSongTransaction}
                            value='Confirm' />
                        <input type="button" 
                            id="delete-song-cancel-button" 
                            className="modal-button" 
                            onClick={store.hideDeleteSongModal}
                            value='Cancel' />
                    </div>
                </div>
        </div>
    );
}
export default DeleteSongModal;
