import jsTPS_Transaction from "../common/jsTPS.js"
import api from '../api'
import { useContext } from 'react'
import { GlobalStoreContext } from '../store'
/**
 * DeleteSong_Transaction
 * 
 * This class represents a transaction that works with deleting a song. 
 * It will be managed by the transaction stack.
 * 
 * @author McKilla Gorilla
 * @author ?
 */
export default class AddSong_Transaction extends jsTPS_Transaction {

    /*
    constructor(initID, initListLength, initSong, initIndex) {
        super();
        this.listID = initID;
        this.songInfo = initSong;
        this.index = initIndex;
        this.listLength = initListLength;
    }
    */

    constructor(initList, initSong, initStore) {
        super();
        this.list = initList;
        this.listID = initList._id;
        this.songInfo = initSong;
        this.store = initStore
        //this.store = useContext(GlobalStoreContext);
    }

    /*
    // DELETE SONG AT GIVEN INDEX FROM LIST
    doTransaction() {
        async function asyncCreateNewSong(id, songInfo){
            if(!songInfo){songInfo = {"title": "Untitled", "artist": "Unknown", "youTubeId": "yvjvLqfawpk"}} 
            let resposne = await api.postSong(id, songInfo)
        }
        asyncCreateNewSong(this.listID, this.songInfo);
    }
    */

    // DELETE SONG AT GIVEN INDEX FROM LIST
    doTransaction() {
        async function asyncCreateNewSong(list, songInfo){
            if(!songInfo){songInfo = {"title": "Untitled", "artist": "Unknown", "youTubeId": "yvjvLqfawpk"}} 
            list.songs.push(songInfo)
            let response = await api.postSong(list._id, list.songs)
        }
        asyncCreateNewSong(this.list, this.songInfo);
    }

    // RESTORE DELETED SONG AT LAST KNOWN INDEX
    undoTransaction() {

        console.log("UNDOING ADD TRANSACTION")
        //this.app.deleteSong(this.app.state.currentList.songs.length-1)
        async function asyncUndoCreate(id, index){
            console.log("Index = " + index)
            let response = await api.deleteSongUndo(id, index)
        }
        // store.deleteSong(id)
        asyncUndoCreate(this.listID, this.listLength-1);
    }
}
